import React, { useState } from 'react';
import { TabContent, TabPane, Nav,
         NavItem, NavLink, Card, Button,
         CardTitle, CardText, Row, Col,
         Container } from 'reactstrap';
import classnames from 'classnames';
import FlightFlat from '../Projects/FlightFlat';
import Deer from '../Projects/Deer';


const Showcase = (props) => {
  const [activeTab, setActiveTab] = useState('1');

  const toggle = tab => {
    if(activeTab !== tab) setActiveTab(tab);
  }

  return (
      <div>
        <Nav tabs>
          <NavItem>
            <NavLink
              className={classnames({ active: activeTab === '1' })}
              onClick={() => { toggle('1'); }}
            >
              Home
            </NavLink>
          </NavItem>
          <NavItem>
            <NavLink
              className={classnames({ active: activeTab === '2' })}
              onClick={() => { toggle('2'); }}
            >
              About Me
            </NavLink>
          </NavItem>
          <NavItem>
            <NavLink
              className={classnames({ active: activeTab === '3' })}
              onClick={() => { toggle('3'); }}
            >
              Projects
            </NavLink>
          </NavItem>
        </Nav>

      <TabContent activeTab={activeTab} style={{height:'100vh', width:'100vw', overflow:'hidden'}}>

        <TabPane tabId="1">
          <Row>
            <Col xs='4' style={{height:'100vh', backgroundColor: '#ffffff'}}>

            </Col>
            <Col style={{backgroundColor: '#00ff00'}}>
              <Deer/>
            </Col>
          </Row>
        </TabPane>

        <TabPane tabId="2">

        </TabPane>

        <TabPane tabId="3">

        </TabPane>
      </TabContent>
    </div>
  );
}

export default Showcase;
