/* global fetch */
import React, {Component} from 'react';
import {render} from 'react-dom';
import {StaticMap} from 'react-map-gl';
import DeckGL from '@deck.gl/react';
import {GeoJsonLayer, ArcLayer, ScatterplotLayer} from '@deck.gl/layers';
import {scaleQuantile} from 'd3-scale';

import tokens from '../secret/tokens.json';
import airports from './DataViz/Data/airports.json';
import routes from './DataViz/Data/routes.json';
// Set your mapbox token here
const MAPBOX_TOKEN = tokens.mapbox; // eslint-disable-line

// Source data GeoJSON
const DATA_URL =
  'https://raw.githubusercontent.com/visgl/deck.gl-data/master/examples/arc/counties.json'; // eslint-disable-line

const INITIAL_VIEW_STATE = {
  longitude: 0,
  latitude: 30,
  zoom: 1.5,
  maxZoom: 15,
  pitch: 30,
  bearing: 30
};

/* eslint-disable react/no-deprecated */
export default class FlightFlat extends Component {
  constructor(props) {
    super(props);

    this.state = {
      viewState: INITIAL_VIEW_STATE
    };

    this._onViewStateChange = this._onViewStateChange.bind(this);
  }


  render() {
    const {mapStyle = 'mapbox://styles/mapbox/dark-v9'} = this.props;

    const layers = [
      new ScatterplotLayer({
        id: 'airports',
        data: airports,
        pickable: true,
        opacity: 1,
        stroked: true,
        filled: true,
        radiusScale: 6,
        getRadius: 3000,
        getPosition: d => d.position,
        getFillColor: [113, 200, 235],
        transitions: {
          getRadius: {
            type: 'interpolation',
            duration: 5000,
            easing: value => value * 1.1 // grow from size 0
          },
          getFillColor: {
            duration: 3000,
            enter: value => [0, 0, 0]
          }
        },
      }),
      new ArcLayer({
        id: 'flights',
        data: routes,
        pickable: true,
        getHeight: () => Math.random() * 0.3 + 0.3,
        getWidth: 0.2,
        opacity: 0.7,
        getSourcePosition: d => d.from,
        getTargetPosition: d => d.to,
        getSourceColor: [0, 207, 82],
        getTargetColor: [0, 198, 144],
        transitions: {
          getSourceColor: {
            duration: 5000,
            enter: value => [0, 0, 0]
          },
          getTargetColor: {
            duration: 10000,
            enter: value => [0,0,0]
          }
        },
      })
    ];

    return (
        <DeckGL
          position='relative'
          layers={layers}
          initialViewState={INITIAL_VIEW_STATE}
          controller={true}
          parameters={{
            clearColor:[0.04,0.04,0.04,0.5]
          }}
        >
          <StaticMap
            position='relative'
            reuseMaps
            mapStyle={mapStyle}
            preventStyleDiffing={true}
            mapboxApiAccessToken={MAPBOX_TOKEN}
          />

        </DeckGL>
    );
  }
}
