import React, { useState } from 'react';
import { TabContent, TabPane, Nav,
         NavItem, NavLink, Card, Button,
         CardTitle, CardText, CardBody, CardSubtitle,
         Row, Col, Container } from 'reactstrap';
import classnames from 'classnames';
import FlightFlat from '../Projects/FlightFlat';
import Deer from '../Projects/Deer';
import Wave from '../Projects/Wave';
import AboutMe from '../AboutMe/AboutMe';

export default class Tab extends React.Component<any, any>   {
  constructor(props : any) {
    super(props);

    this.state = {
      activeTab : '2'
    }

    this._toggle = this._toggle.bind(this);
    this._changeBackgroundColour = this._changeBackgroundColour.bind(this);

  }

  _toggle(tab : string) {
    this._changeBackgroundColour(tab);
    if (this.state.activeTab !== tab) {
      this.setState({ activeTab: tab });
    }
  }

  _changeBackgroundColour(tab : string) {
    var colour : string[];

    switch (tab) {
      case '1':
        colour = ["#5680E9", "#84CEEB", " #C1C8E4", "#5AB9EA" , "#8860D0"];
        break;
      case '2':
        colour = ["#2a67ad", "#262449","#68f8fc","#9386fc","#71a8fc"];
        break;
      case '3':
        colour = ["#00ff00","#00ff00","#00ff00","#00ff00","#00ff00"];
        break
      default:
        colour = ["#5680E9", "#84CEEB", " #C1C8E4", "#5AB9EA" , "#8860D0"];
    }
    // document.body.style.backgroundImage =
    //   `linear-gradient(to top, ${colour[0]}, ${colour[1]}),
    //    linear-gradient(to right, ${colour[2]}, ${colour[3]} 30%),
    //    linear-gradient(to left, ${colour[4]}, ${colour[3]}),
    //    linear-gradient(to bottom, ${colour[0]}, ${colour[2]})`;
  }

  componentDidUpdate() {
    var colour : string[];

    switch (this.state.activeTab) {
      case '1':
      colour = ["#5680E9", "#84CEEB", " #C1C8E4", "#5AB9EA" , "#8860D0"];
      break;
      case '2':
      colour = ["#2a67ad", "#262449","#68f8fc","#9386fc","#71a8fc"];
      break;
      case '3':
      colour = ["#00ff00","#00ff00","#00ff00","#00ff00","#00ff00"];
      break
      default:
      colour = ["#5680E9", "#84CEEB", " #C1C8E4", "#5AB9EA" , "#8860D0"];
    }

    document.body.style.backgroundImage =
      `linear-gradient(to top, ${colour[0]}, ${colour[1]}),
       linear-gradient(to right, ${colour[2]}, ${colour[3]} 30%),
       linear-gradient(to left, ${colour[4]}, ${colour[3]}),
       linear-gradient(to bottom, ${colour[0]}, ${colour[2]})`;
  }

  render() {

    return (
        <div style={{height:'100vh', display:'flex', flexDirection:'column'}}>
          <Nav tabs>
            <NavItem>
              <NavLink
                className={classnames({ active: this.state.activeTab === '1' })}
                onClick={() => { this._toggle('1'); }}
              >
                Home
              </NavLink>
            </NavItem>
            <NavItem>
              <NavLink
                className={classnames({ active: this.state.activeTab === '2' })}
                onClick={() => { this._toggle('2'); }}
              >
                About Me
              </NavLink>
            </NavItem>
            <NavItem>
              <NavLink
                className={classnames({ active: this.state.activeTab === '3' })}
                onClick={() => { this._toggle('3'); }}
              >
                Projects
              </NavLink>
            </NavItem>
          </Nav>
        <Container fluid className="flex-grow-1">
          <TabContent activeTab={this.state.activeTab}>

            <TabPane tabId="1">
              {
                this.state.activeTab === '1'
                ?
                  <Row>
                    <Deer/>
                    <Col
                      xs={{size : 6, offset: 3}}
                      sm={{size : 4, offset: 6}}
                      md={{size : 3, offset: 8}}
                      style={{position:'absolute', top:'30%'}}
                    >
                      <Card style={{display:'flex',
                                    justifyContent:'center',
                                    backgroundColor:'rgba(255, 255, 255, 0.4)'}}>
                        <CardBody>
                          <h2>Hoang Vu</h2>
                          <h3> Looking for a summer internship </h3>
                        </CardBody>
                      </Card>
                    </Col>
                  </Row>
                : null
            }
            </TabPane>

            <TabPane classNames='flex-grow-1' tabId="2" >
              {
                  this.state.activeTab === '2'
                  ? <Container xs="fluid" md='10' style={{marginTop : '5%'}} >
                      <Row>
                        <Col xs='3' md='4' style={{backgroundColor:"#00ff00"}}>
                        </Col>
                        <Col>
                          <AboutMe/>
                        </Col>
                      </Row>
                    </Container>
                  : null
              }
            </TabPane>

            <TabPane tabId="3">
            {

            }
            </TabPane>
          </TabContent>
        </Container>
      </div>
    );
  }
}
