/* global fetch */
import React, {Component} from 'react';
import {render} from 'react-dom';
import {StaticMap} from 'react-map-gl';
import DeckGL from '@deck.gl/react';
import {GeoJsonLayer, ArcLayer} from '@deck.gl/layers';
import {scaleQuantile} from 'd3-scale';

import tokens from '../secret/tokens.json';
import stadiums from './DataViz/Data/stadiums.json';
import transfers from './DataViz/Data/2019/english_transfers.json';

// Set your mapbox token here
const MAPBOX_TOKEN = tokens.mapbox; // eslint-disable-line

// Source data GeoJSON
const DATA_URL =
  'https://raw.githubusercontent.com/visgl/deck.gl-data/master/examples/arc/counties.json'; // eslint-disable-line

export const inFlowColors = [
  [255, 255, 204],
  [199, 233, 180],
  [127, 205, 187],
  [65, 182, 196],
  [29, 145, 192],
  [34, 94, 168],
  [12, 44, 132]
];

export const outFlowColors = [
  [255, 255, 178],
  [254, 217, 118],
  [254, 178, 76],
  [253, 141, 60],
  [252, 78, 42],
  [227, 26, 28],
  [177, 0, 38]
];

const INITIAL_VIEW_STATE = {
  longitude: -100,
  latitude: 40.7,
  zoom: 3,
  maxZoom: 15,
  pitch: 30,
  bearing: 30
};

/* eslint-disable react/no-deprecated */
export default class FootballTransfer extends Component {
  constructor(props) {
    super(props);
    this.state = {};

  }

  _renderLayers() {
    const {data, strokeWidth = 2} = this.props;

    return [
      // new GeoJsonLayer({
      //   id: 'geojson',
      //   data,
      //   stroked: false,
      //   filled: true,
      //   getFillColor: [0, 0, 0, 0],
      //   onHover: this._onHoverCounty,
      //   onClick: this._onSelectCounty,
      //   pickable: true
      // }),
      new ArcLayer({
        id: 'arc',
        data: transfers,
        getSourcePosition: d => stadiums[d.from],
        getTargetPosition: d => stadiums[d.to],
        getSourceColor: [0, 255, 0],
        getTargetColor: [0, 0, 255],
        getWidth: 5
      })
    ];
  }

  render() {
    const {mapStyle = 'mapbox://styles/mapbox/light-v9'} = this.props;

    return (
      <DeckGL layers={this._renderLayers()} initialViewState={INITIAL_VIEW_STATE} controller={true}>
        <StaticMap
          reuseMaps
          mapStyle={mapStyle}
          preventStyleDiffing={true}
          mapboxApiAccessToken={MAPBOX_TOKEN}
        />

        {this._renderTooltip}
      </DeckGL>
    );
  }
}
