/* global fetch */
import React, {Component} from 'react';
import {render} from 'react-dom';
import {StaticMap} from 'react-map-gl';
import DeckGL from '@deck.gl/react';
import {GeoJsonLayer, ArcLayer, ScatterplotLayer} from '@deck.gl/layers';
import {scaleQuantile} from 'd3-scale';

import tokens from '../secret/tokens.json';
import airports from './DataViz/Data/airports.json';
// Set your mapbox token here
const MAPBOX_TOKEN = tokens.mapbox; // eslint-disable-line

// Source data GeoJSON
const DATA_URL =
  'https://raw.githubusercontent.com/visgl/deck.gl-data/master/examples/arc/counties.json'; // eslint-disable-line

const INITIAL_VIEW_STATE = {
  longitude: -100,
  latitude: 40.7,
  zoom: 3,
  maxZoom: 15,
  pitch: 30,
  bearing: 30
};

/* eslint-disable react/no-deprecated */
export default class FootballTransfer extends Component {
  constructor(props) {
    super(props);
  }

  render() {
    const {mapStyle = 'mapbox://styles/mapbox/light-v9'} = this.props;

    const layers = [
      new ScatterplotLayer({
        id: 'airports',
        data: airports,
        pickable: true,
        opacity: 1,
        stroked: true,
        filled: true,
        radiusScale: 6,
        lineWidthMinPixels: 1,
        getPosition: d => d.position,
        getFillColor: d => [255, 140, 0],
        getLineColor: d => [0, 0, 0],
      })
    ];

    return (
      <DeckGL
        layers={layers}
        initialViewState={INITIAL_VIEW_STATE}
        controller={true}
      >
        <StaticMap
          reuseMaps
          mapStyle={mapStyle}
          preventStyleDiffing={true}
          mapboxApiAccessToken={MAPBOX_TOKEN}
        />

      </DeckGL>
    );
  }
}
