import React, { useState } from 'react';
import { TabContent, TabPane, Nav,
         NavItem, NavLink, Card, Button,
         CardTitle, CardText, CardBody, CardSubtitle,
         Row, Col, Container } from 'reactstrap';
import classnames from 'classnames';
import FlightFlat from '../Projects/FlightFlat';
import Deer from '../Projects/Deer';
import Wave from './Wave';

export default class Tab extends React.Component<any, any>   {
  constructor(props : any) {
    super(props);

    this._toggle = this._toggle.bind(this);
    this.state = {
      activeTab : '1'
    }
  }

  _toggle(tab : any) {
    if(this.state.activeTab !== tab) {
      this.setState({ activeTab: tab });
    }
  }

  render() {

    return (
        <div style={{height:'100vh', overflow:'hidden', display:'flex', flexDirection:'column'}}>
          <Nav tabs>
            <NavItem>
              <NavLink
                className={classnames({ active: this.state.activeTab === '1' })}
                onClick={() => { this._toggle('1'); }}
              >
                Home
              </NavLink>
            </NavItem>
            <NavItem>
              <NavLink
                className={classnames({ active: this.state.activeTab === '2' })}
                onClick={() => { this._toggle('2'); }}
                href="/"
              >
              <a href="#"/>
                About Me
              </NavLink>
            </NavItem>
            <NavItem>
              <NavLink
                className={classnames({ active: this.state.activeTab === '3' })}
                onClick={() => { this._toggle('3'); }}
              >
                Projects
              </NavLink>
            </NavItem>
          </Nav>
        <Container fluid style={{height:'100vh'}}>
          <TabContent activeTab={this.state.activeTab}>

            <TabPane tabId="1" style={{height:'100%'}}>
              {
                this.state.activeTab === '1'
                ?
                  <Row style={{height:'100vh'}}>
                    <Deer/>
                    <Col xs={{size : 6, offset: 3}} sm={{size : 4, offset: 6}} md={{size : 3, offset: 8}}>
                      <Card style={{top:'30%',
                                    display:'flex',
                                    justifyContent:'center',
                                    backgroundColor:'rgba(255, 255, 255, 0.4)'}}>
                        <CardBody>
                          <h2>Hoang Vu</h2>
                          <h3> Looking for a summer internship </h3>
                        </CardBody>
                      </Card>
                    </Col>
                  </Row>
                : null
            }
            </TabPane>

            <TabPane tabId="2">
              {
                  this.state.activeTab === '2'
                  ? <Wave/>
                  : null
              }
              <Wave/>
            </TabPane>

            <TabPane tabId="3">
            {

            }
            </TabPane>
          </TabContent>
        </Container>
      </div>
    );
  }
}
