import React, { useState } from 'react';
import { TabContent, TabPane, Nav,
         NavItem, NavLink, Card, Button,
         CardTitle, CardText, Row, Col,
         Container } from 'reactstrap';
import classnames from 'classnames';
import FlightFlat from '../Projects/FlightFlat';
import Deer from '../Projects/Deer';


const Showcase = (props) => {
  const [activeTab, setActiveTab] = useState('1');

  const toggle = tab => {
    if(activeTab !== tab) setActiveTab(tab);
  }

  return (
    <Container fluid style={{height:'100vh'}}>
      <Row>
        <Nav tabs>
          <NavItem>
            <NavLink
              className={classnames({ active: activeTab === '1' })}
              onClick={() => { toggle('1'); }}
            >
              Home
            </NavLink>
          </NavItem>
          <NavItem>
            <NavLink
              className={classnames({ active: activeTab === '2' })}
              onClick={() => { toggle('2'); }}
            >
              About Me
            </NavLink>
          </NavItem>
          <NavItem>
            <NavLink
              className={classnames({ active: activeTab === '3' })}
              onClick={() => { toggle('3'); }}
            >
              Projects
            </NavLink>
          </NavItem>
        </Nav>
      </Row>
      <TabContent activeTab={activeTab}>

        <TabPane tabId="1">
          <Deer/>
        </TabPane>

        <TabPane tabId="2">
          <Row style={{height:'90vh'}}>
            <div id='map' style={{position:'relative', width:'100%',height:'100%'}}>
              <FlightFlat/>
            </div>
          </Row>
        </TabPane>

        <TabPane tabId="3">

        </TabPane>
      </TabContent>
    </Container>
  );
}

export default Showcase;
