import React from 'react';
import { Row, Col, Container } from 'reactstrap';
import Flippy, { FrontSide, BackSide } from 'react-flippy';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCode, faCodeBranch, faGraduationCap } from "@fortawesome/free-solid-svg-icons";
import { faCss3, faJsSquare } from "@fortawesome/free-brands-svg-icons";
import { library } from '@fortawesome/fontawesome-svg-core'
import cardDetails from './cardDetails.json';
import Contact from './Contact';


library.add(faCode, faCodeBranch, faJsSquare, faCss3, faGraduationCap);

interface IFace {
  "title" : string,
  "subtitle" : string,
  "body" : string
};

interface ICard {
  "id" : number,
  "front" : IFace,
  "back" : IFace,
  "categories" ?: string[]
}

export default class FlipCard extends React.Component<{}, {content : any}> {

  getIcon(kind : string){
    switch(kind) {
      case "edu" : return <FontAwesomeIcon className="svg-icons" icon="graduation-cap"/>;
      case "code": return <FontAwesomeIcon className="svg-icons" icon="code"/>;
      case "branch": return <FontAwesomeIcon className="svg-icons" icon="code-branch"/>;
      case "js" : return <FontAwesomeIcon className="svg-icons" icon={['fab', "js-square"]}/>
    }
  }


  render() {
    const flipCards = cardDetails.map( (card : ICard) => {
      return(
        <Col xs='12' md={{size: 10, offset:1}} lg={{size:8, offset : 2}} xl={{size: 6,offset:0}}>
          <Flippy
            flipOnHover={true}
            flipOnClick={false}
            flipDirection="horizontal"
            >
            <FrontSide
              style={{
                backgroundColor: "rgba(255, 255, 255, 0.5)",
              }}
            >
              <h2> {card.front.title} </h2>
              <h4> {card.front.subtitle} </h4>
              <p> {card.front.body} </p>

              { card.categories ? card.categories.map(item => this.getIcon(item))  : null }
            </FrontSide>
            <BackSide
              style={{
                backgroundColor: '#175852'
              }}
            >
              <h2> {card.back.title} </h2>
              <h4> {card.back.subtitle} </h4>
              <p> {card.back.body} </p>
            </BackSide>
          </Flippy>
        </Col>
    )})

    return (

      <Container id="about-me-wrapper" xs="fluid" md='10' className='flex-column d-flex h-100'>
        <Row xs={12} md={{size:10, offset:2}} className='h-100' style={{alignItems:'center'}}>
          <Col className='fixed' xs='12' md='4'>
            <Contact/>
          </Col>
          <Col className='h-75 overflow-auto'>
            <Row className='h-100'>
              {flipCards}
            </Row>
          </Col>
        </Row>
      </Container>
    )
  }
}
