import React from 'react';
import { Row, Col, Container } from 'reactstrap';
import Flippy, { FrontSide, BackSide } from 'react-flippy';

import cardDetails from './cardDetails.json';
import Contact from './Contact';

export default class FlipCard extends React.Component<{}, {content : any}> {

  render() {
    const flipCards = cardDetails.map( (card : any) => {
      return(
        <Col xs='10' md='8' lg='6'>
          <Flippy
            flipOnHover={true}
            flipOnClick={false}
            flipDirection="horizontal"
            >
            <FrontSide
              style={{
                backgroundColor: "rgba(255, 255, 255, 0.5)",
              }}
            >
              <h2> {card.front.title} </h2>
              <h4> {card.front.subtitle} </h4>
              <p> {card.front.body} </p>
            </FrontSide>
            <BackSide
              style={{
                backgroundColor: '#175852'
              }}
            >
              <h2> {card.back.title} </h2>
              <h3> {card.back.subtitle} </h3>
              <p> {card.back.body} </p>
            </BackSide>
          </Flippy>
        </Col>
    )})

    return (

      <Container id="about-me-wrapper" xs="fluid" md='10'>
        <Row xs={12} md={10} style={{height: '100%', display: 'flex', alignItems:'center'}}>
          <Col className='fixed' xs='12' sm='12' md='4' style={{height: '40vh'}}>
            <Contact/>
          </Col>
          <Col style={{height:'80vh', overflowY:'scroll'}}>
            <Row>
              {flipCards}
            </Row>
          </Col>
        </Row>
      </Container>
    )
  }
}
