import React, { useState } from 'react';
import { Row, Col, Container } from 'reactstrap';
import Flippy, { FrontSide, BackSide } from 'react-flippy';

import cardDetails from './cardDetails.json';

export default class FlipCard extends React.Component<{}, {content : any}> {
  constructor(props : any) {
    super(props);

  }


  render() {
    const flipCards = cardDetails.map( (card : any) => {
      return(
        <Col xs='10' md='12' lg='12' style={{backgroundColor: "#ff0000"}}>
          <Flippy
            flipOnHover={true}
            flipOnClick={false}
            flipDirection="horizontal"
            >
            <FrontSide
              style={{
                backgroundColor: '#41669d',
              }}
            >
              <h1> {card.front.title} </h1>
              <h2> {card.front.subtitle} </h2>
              <p> {card.front.body} </p>
            </FrontSide>
            <BackSide
              style={{
                backgroundColor: '#175852'
              }}
            >
              <h1> {card.back.title} </h1>
              <h2> {card.back.subtitle} </h2>
              <p> {card.back.body} </p>
            </BackSide>
          </Flippy>
        </Col>
    )})

    return (
      <div style={{display:'flex'}}>
        {flipCards}
      </div>
    )
  }
}
