/* eslint-disable no-unused-vars */
import React, { PureComponent, Component, useState, useEffect} from 'react';
import { render} from 'react-dom';
import DeckGL from '@deck.gl/react';
import { COORDINATE_SYSTEM, OrbitView, LinearInterpolator} from '@deck.gl/core';
import { PointCloudLayer, LineLayer, ScatterplotLayer } from '@deck.gl/layers';
import { scaleLinear } from "d3-scale";


import puddle from './components/puddle.json';
import droplet from './components/droplet.json';


const colorScale = scaleLinear()
  .domain([0.29, 0.68])
  .range(["#ffedea", "#ff5233"]);

const INITIAL_VIEW_STATE = {
  target: [0, -40, 10],
  rotationX: 20,
  rotationOrbit: 5,
  fov: 50,
  minZoom: 3.5,
  maxZoom: 15,
  zoom: 4
};

const transitionInterpolator = new LinearInterpolator(['rotationOrbit']);


export default class Wave extends Component {
  constructor(props) {
    super(props);

    this.state = {
      viewState: INITIAL_VIEW_STATE,
    };

    this._onViewStateChange = this._onViewStateChange.bind(this);
  }

  _onViewStateChange({viewState}) {
    this.setState({viewState});
  }

  render() {
    const {viewState} = this.state;

    const layers = [
      new PointCloudLayer({
        id: 'wave',
        data: puddle,
        getPosition: d => d.position,
        coordinateSystem: COORDINATE_SYSTEM.CARTESIAN,
        getColor: [255, 255, 255],
        opacity: 0.5,
        pointSize: 5,
        transitions: {
          opacity: {
            duration: 3000
          },
          getColor: {
            duration: 3000,
            enter: value => [0,0,0]
          },
          getPosition : {
            duration: 30000,
            type: 'spring',
            stiffness: 0.005,
            damping: 0.005,
            enter: value => [value[0], value[1], 3 * Math.cos(0.5 * Math.sqrt(value[1] ** 2 + value[0] ** 2))]
          },
        }
      })
    ];

    return (
      <DeckGL
        views={new OrbitView()}
        viewState={viewState}
        controller={true}
        onViewStateChange={this._onViewStateChange}
        layers={layers}
        parameters={{
          clearColor: [0, 0, 0, 0]
        }}
      >
      </DeckGL>
    );
  }
}
