/* eslint-disable no-unused-vars */
import React, { PureComponent, Component} from 'react';
import { render} from 'react-dom';
import DeckGL from '@deck.gl/react';
import { COORDINATE_SYSTEM, OrbitView, LinearInterpolator} from '@deck.gl/core';
import { PointCloudLayer, LineLayer } from '@deck.gl/layers';

import vertices from './PointCloud/vertices.json';
import edges from './PointCloud/edges.json';

const LAZ_SAMPLE =
  'https://raw.githubusercontent.com/visgl/deck.gl-data/master/examples/point-cloud-laz/indoor.0.1.laz';

const INITIAL_VIEW_STATE = {
  target: [0, 10, 0],
  rotationX: 0,
  rotationZ : 90,
  rotationOrbit: 0,
  orbitAxis: 'Y',
  fov: 50,
  minZoom: 0,
  maxZoom: 10,
  zoom: 3.6
};

const transitionInterpolator = new LinearInterpolator(['rotationOrbit']);

export default class DeerCloud extends Component {
  constructor(props) {
    super(props);

    this.state = {
      viewState: INITIAL_VIEW_STATE
    };

    this._onLoad = this._onLoad.bind(this);
    this._onViewStateChange = this._onViewStateChange.bind(this);
    this._rotateCamera = this._rotateCamera.bind(this);
  }

  _onViewStateChange({viewState}) {
    this.setState({viewState});
  }

  _rotateCamera() {
    const {viewState} = this.state;
    this.setState({
      viewState: {
        ...viewState,
        rotationOrbit: viewState.rotationOrbit + 120,
        transitionDuration: 2400,
        transitionInterpolator,
        onTransitionEnd: this._rotateCamera
      }
    });
  }

  _onLoad({header, loaderData, progress}) {
    // metadata from LAZ file header
    const mins = [10, 10, 10];
    const maxs = [10, 10, 10]

    if (mins && maxs) {
      // File contains bounding box info
      this.setState(
        {
          viewState: {
            ...this.state.viewState,
            target: [(mins[0] + maxs[0]) / 2, (mins[1] + maxs[1]) / 2, (mins[2] + maxs[2]) / 2],
            /* global window */
            zoom: Math.log2(window.innerWidth / (maxs[0] - mins[0])) - 1
          }
        },
        this._rotateCamera
      );
    }

    if (this.props.onLoad) {
      this.props.onLoad({count: header.vertexCount, progress: 1});
    }
  }

  render() {
    const {viewState} = this.state;

    const layers = [
      new PointCloudLayer({
       id: 'laz-point-cloud-layer',
       data: LAZ_SAMPLE,
       onDataLoad: this._onLoad,
       coordinateSystem: COORDINATE_SYSTEM.CARTESIAN,
       getNormal: [0, 1, 0],
       getColor: [255, 255, 255],
       opacity: 0.5,
       pointSize: 0.01
     }),
      new PointCloudLayer({
        id: 'point-cloud-layer',
        data: vertices,
        onDataLoad: this._onLoad,
        getPosition: d => d.position,
        coordinateSystem: COORDINATE_SYSTEM.CARTESIAN,
        getColor: [255, 0, 0],
        opacity: 0.5,
        pointSize: 2
      }),
      new LineLayer({
       id: 'arc-layer',
       data: edges,
       coordinateSystem: COORDINATE_SYSTEM.CARTESIAN,
       pickable: true,
       getWidth: 1,
       getSourcePosition: edge => edge.from,
       getTargetPosition: edge => edge.to,
       getColor: [255, 255, 255],
     })
    ];

    return (
      <DeckGL
        views={new OrbitView()}
        viewState={viewState}
        controller={true}
        onViewStateChange={this._onViewStateChange}
        layers={layers}
        parameters={{
          clearColor: [0, 0, 0, 0.55]
        }}
      >
      </DeckGL>
    );
  }
}
