import React, { useState } from 'react';
import { TabContent, TabPane, Nav,
         NavItem, NavLink, Card, Button,
         CardTitle, CardText, CardBody, CardSubtitle,
         Row, Col, Container } from 'reactstrap';
import classnames from 'classnames';
import FlightFlat from '../Projects/FlightFlat';
import Deer from '../Projects/Deer';


const Showcase = (props) => {
  const [activeTab, setActiveTab] = useState('1');

  const toggle = tab => {
    if(activeTab !== tab) setActiveTab(tab);
  }

  return (
      <div>
        <Nav tabs>
          <NavItem>
            <NavLink
              className={classnames({ active: activeTab === '1' })}
              onClick={() => { toggle('1'); }}
            >
              Home
            </NavLink>
          </NavItem>
          <NavItem>
            <NavLink
              className={classnames({ active: activeTab === '2' })}
              onClick={() => { toggle('2'); }}
            >
              About Me
            </NavLink>
          </NavItem>
          <NavItem>
            <NavLink
              className={classnames({ active: activeTab === '3' })}
              onClick={() => { toggle('3'); }}
            >
              Projects
            </NavLink>
          </NavItem>
        </Nav>
      <Container fluid>
        <TabContent activeTab={activeTab} style={{height:'100%', overflow:'hidden'}}>

          <TabPane tabId="1">
            <Row>
              <Col xs='8'>
                <Deer/>
              </Col>
              <Col>
              <Card style={{top:'30%'}}>
                <CardBody>
                  <CardTitle>Card title</CardTitle>
                  <CardSubtitle>Card subtitle</CardSubtitle>
                  <CardText>Some quick example text to build on the card title and make up the bulk of the card's content.</CardText>
                </CardBody>
                </Card>
              </Col>
            </Row>
          </TabPane>

          <TabPane tabId="2">

          </TabPane>

          <TabPane tabId="3">

          </TabPane>
        </TabContent>
      </Container>
    </div>
  );
}

export default Showcase;
