import React, { useState } from 'react';
import { TabContent, TabPane, Nav,
         NavItem, NavLink, Card, Button,
         CardTitle, CardText, CardBody, CardSubtitle,
         Row, Col, Container } from 'reactstrap';
import classnames from 'classnames';
import FlightFlat from '../Projects/FlightFlat';
import Deer from '../Projects/Deer';
import Wave from './Wave';


const Tab = (props) => {
  const [activeTab, setActiveTab] = useState('1');

  const toggle = tab => {
    if(activeTab !== tab) setActiveTab(tab);
  }

  return (
      <div style={{height:'100vh', overflow:'hidden', display:'flex', flexDirection:'column'}}>
        <Nav tabs>
          <NavItem>
            <NavLink
              className={classnames({ active: activeTab === '1' })}
              onClick={() => { toggle('1'); }}
            >
              Home
            </NavLink>
          </NavItem>
          <NavItem>
            <NavLink
              className={classnames({ active: activeTab === '2' })}
              onClick={() => { toggle('2'); }}
            >
              About Me
            </NavLink>
          </NavItem>
          <NavItem>
            <NavLink
              className={classnames({ active: activeTab === '3' })}
              onClick={() => { toggle('3'); }}
            >
              Projects
            </NavLink>
          </NavItem>
        </Nav>
      <Container fluid style={{height:'100vh'}}>
        <TabContent activeTab={activeTab}>

          <TabPane tabId="1" style={{height:'100%'}}>
            <Row style={{height:'100vh'}}>
              <Col xs='9'>
                <Deer/>

              </Col>
              <Col xs='3'>
                <Card style={{top:'30%', display:'flex', justifyContent:'center'}}>
                  <CardBody>
                    <h2>Hoang Vu</h2>
                    <h2>First Year Computing Student</h2>
                    <h4> Looking for a summer internship </h4>
                  </CardBody>
                </Card>
              </Col>
            </Row>
          </TabPane>

          <TabPane tabId="2">

          </TabPane>

          <TabPane tabId="3">

          </TabPane>
        </TabContent>
      </Container>
    </div>
  );
}

export default Tab;
