import React from 'react';
import ReactDOM from 'react-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { library } from '@fortawesome/fontawesome-svg-core';
import { faCheckSquare, faUniversity , faCoffee} from '@fortawesome/free-solid-svg-icons';
import { VerticalTimeline, VerticalTimelineElement }  from 'react-vertical-timeline-component';
import 'react-vertical-timeline-component/style.min.css';

import timeline from './timeline.json';

library.add(faCheckSquare, faCoffee);

const theme = ['#05386B', '#379683', '#5CDB95', '#8EE4AF'];

const getColour = (id : number) => {
  return theme[id % 3];
}

class Timeline extends React.Component {
    constructor(props : any) {
      super(props);
    }

    render() {
      const timelineitems = timeline.map( item => {
        var colour = getColour(item.id);
        return(
          <VerticalTimelineElement
            className="vertical-timeline-element--work"
            contentStyle={{ background: colour, color: '#ffffff' }}
            contentArrowStyle={{ borderRight: `7px solid  ${colour}` }}
            date={item.timespan}
            iconStyle={{textAlign: 'center', background: colour, color: '#FFFFFF' }}
            icon={<FontAwesomeIcon icon={faUniversity} size="lg" /> }
          >
            <h3> {item.title} </h3>
            <h4> {item.subtitle} </h4>
            <p> {item.text} </p>
          </VerticalTimelineElement>
        )
      })
      return(
        <VerticalTimeline>
          {timelineitems}
        </VerticalTimeline>
      );
    }


}

export default Timeline;
