import React, { Component } from 'react';
import { StaticMap } from 'react-map-gl';
import DeckGL from '@deck.gl/react';
import { PointCloudLayer } from '@deck.gl/layers';
import { MapView } from '@deck.gl/core';
import GL from '@luma.gl/constants';

import tokens from '../secret/tokens.json';
import vertices from './PointCloud/deer.json';


// Set your mapbox token here
const MAPBOX_TOKEN = tokens.mapbox; // eslint-disable-line

const INITIAL_VIEW_STATE = {
  longitude: 0,
  latitude: 30,
  zoom: 1.5,
  pitch: 30,
  bearing: 15
};



/* eslint-disable react/no-deprecated */
export default class Deer extends Component {
  constructor(props) {
    super(props);

    this.state = {
      viewState: INITIAL_VIEW_STATE,
      hoveredObject: null
    };

    this._onHover = this._onHover.bind(this);
    this._renderTooltip = this._renderTooltip.bind(this);
  }

  _onHover({x, y, object}) {
    this.setState({x, y, hoveredObject: object});
  }

  _renderTooltip() {
    const {hoveredObject, x, y} = this.state || {};
    return hoveredObject && (
      <div id='mapTooltip' style={{left: x, top: y}}>
        <h4> { hoveredObject.name } </h4>
      </div>
    );
  }

  render() {

    const layers = [
      new PointCloudLayer({
        id: d => d.id,
        data: vertices,
        pickable: true,
        getRadius: 100,
        getColour: [255, 0 ,0],
        getPosition: d => d.position
      })
    ];

    return (
        <DeckGL
          position='relative'
          layers={layers}
          initialViewState={INITIAL_VIEW_STATE}
          controller={true}
        >

        </DeckGL>
    );
  }
}
