import React, { useState } from 'react';
import { Row, Col, Container } from 'reactstrap';
import Flippy, { FrontSide, BackSide } from 'react-flippy';

const PATH = './cardDetails.json';

export default class FlipCard extends React.Component<{}, {content : any}> {
  constructor(props : any) {
    super(props);

    this.state = {
        content : null
    };
  }

  componentWillMount() {
    fetch(PATH).then(response => response.json())
               .then(jsonData => this.setState({ content: jsonData}));
  }

  render() {
    const { content } = this.state;

    const flipCards = content.map( (card : any) => {
      return(
        <Col xs='10' md='8' lg='6'>
          <Flippy
            flipOnHover={true}
            flipOnClick={false}
            flipDirection="horizontal"
            >
            <FrontSide
              style={{
                backgroundColor: '#41669d',
              }}
            >
              <h1> {card.front.title} </h1>
              <h2> {card.front.subtitle} </h2>
            </FrontSide>
            <BackSide
              style={{ backgroundColor: '#175852'}}>
              ROCKS
            </BackSide>
          </Flippy>
        </Col>
    )})

    return (
      <Col xs='10' md='8' lg='6'>
        <Flippy
          flipOnHover={true}
          flipOnClick={false}
          flipDirection="horizontal"
          >
          <FrontSide
            style={{
              backgroundColor: '#41669d',
            }}
          >
            RICK
          </FrontSide>
          <BackSide
            style={{ backgroundColor: '#175852'}}>
            ROCKS
          </BackSide>
        </Flippy>
      </Col>
    )
  }
}
