import React from 'react';
import { Row, Col, Collapse } from 'reactstrap';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faLink, faMapMarker, faBolt } from "@fortawesome/free-solid-svg-icons";
import { faGithub, faLinkedin } from "@fortawesome/free-brands-svg-icons";
import { library } from '@fortawesome/fontawesome-svg-core'

library.add(faLink, faBolt, faGithub, faMapMarker, faLinkedin);


export default class Contact extends React.Component<{}, { isOpen : boolean }> {

    constructor(props : {}) {
        super(props);

        this.state = {
            isOpen : false
        }

        this._toggle = this._toggle.bind(this);
        this._updateDimensions = this._updateDimensions.bind(this);
    }

    _toggle() {
        const { isOpen } = this.state;
        this.setState({
            isOpen: !isOpen
        });
    }

    _updateDimensions() {
        this.setState({
            isOpen: window.innerWidth > 548
        });
    }

    componentDidMount() {
       this._updateDimensions();
       window.addEventListener('resize', this._updateDimensions);
    }

    render() {
        const {isOpen} = this.state;
        return(
            <Row className="d-flex justify-content-center"style={{display:'flex', alignItems:'center'}}>
                <Collapse isOpen={isOpen}>
                    <Col xs={{size: 12, offset: 0}} md='12'>
                        <FontAwesomeIcon icon="bolt" size="10x" color="white"/>
                        <br/><br/>
                    </Col>
                    <Col xs={{size: 12, offset: 0}} md='12'>
                        <h2> hoang vu </h2>
                        <br/><br/>
                    </Col>
                    <Col xs={{size: 12, offset: 0}} md='12'>
                        <a href="https://github.com/hoangvu01">
                            <h4> <FontAwesomeIcon icon={['fab', "github"]}/> hoangvu01 </h4>
                        </a>            
                    </Col>
                    <Col xs={{size: 12, offset: 0}} md='12'>
                        <a href="https://hoangvu01.github.io">
                            <h4> <FontAwesomeIcon icon="link"/> hoangvu01.github.io </h4>
                        </a>
                    </Col>
                    <Col xs={{size: 12, offset: 0}} md='12'>
                        <h4> <FontAwesomeIcon icon='map-marker'/>  London, United Kingdom </h4>
                    </Col>
                </Collapse>
                <Collapse isOpen={!isOpen}>
                    <Col xs='12'>
                        <h2> hoang vu </h2>
                    </Col>
                    <Col xs='12'>
                        <Row>
                            <Col xs='3'>
                                <a href="https://github.com/hoangvu01">
                                    <FontAwesomeIcon color="#FFFFFF" size="2x" icon={['fab', "github"]}/>
                                </a>            
                            </Col>
                            <Col xs='3'>
                                <a href="https://hoangvu01.github.io">
                                    <FontAwesomeIcon color="#FFFFFF" size="2x" icon="link"/>
                                </a>
                            </Col>
                            <Col xs='3'>
                                <FontAwesomeIcon color="#FFFFFF" size="2x" icon='map-marker'/>  
                            </Col>
                        </Row>
                    </Col>
                </Collapse>
            </Row>
        )
    }
}
